import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import {CategoryScale} from 'chart.js'; 
Chart.register(CategoryScale);
import axios from 'axios';

const SchoolBarChart = () => {
  const [schoolData, setSchoolData] = useState([]);

  useEffect(() => {
    // Fetch school data from your backend
    axios.get('http://localhost:3002/api/getSchoolData')
      .then((response) => {
        setSchoolData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  // Extract school names and number of students from the data
  const schoolNames = schoolData.map((school) => school.schoolName);
  const numberOfStudents = schoolData.map((school) => school.numberOfStudents);

  // Define the chart data
  const chartData = {
    labels: schoolNames,
    datasets: [
      {
        label: 'Number of Students',
        data: numberOfStudents,
        backgroundColor: 'rgba(75, 192, 192, 0.6)', // Customize the bar color
        borderColor: 'rgba(75, 192, 192, 1)', // Customize the border color
        borderWidth: 1, // Customize the border width
      },
    ],
  };

  // Define the chart options
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // Allow resizing without maintaining aspect ratio
    scales: {
      x: {
        title: {
          display: true,
          text: 'School Names',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Number of Students',
        },
      },
    },
  };

  return (
    <div className="vertical-bar-graph">
      <Bar data={chartData} options={chartOptions} />
    </div>
  );
};

export default SchoolBarChart;
