// GovernmentDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GovernmentDashboard() {
  const [studentData, setStudentData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    schoolName: '',
    age: '',
    caste: '',
    schoolArea: '',
    schoolDistrict: '',
  });

  useEffect(() => {
    // Fetch student data from the API
    axios.get('http://localhost:3002/api/submitStudentData').then((response) => {
      setStudentData(response.data);
      setFilteredData(response.data); // Initialize filtered data with all students
    });
  }, []);

  // Handle filter changes
  useEffect(() => {
    applyFilters();
  }, [filters]);

  const applyFilters = () => {
    // Filter the data based on the selected filters
    let filteredResult = studentData;

    if (filters.schoolName) {
      filteredResult = filteredResult.filter((student) =>
        student.schoolName.toLowerCase().includes(filters.schoolName.toLowerCase())
      );
    }

    if (filters.age) {
      filteredResult = filteredResult.filter((student) => student.age === parseInt(filters.age));
    }

    if (filters.caste) {
      filteredResult = filteredResult.filter((student) =>
        student.caste.toLowerCase().includes(filters.caste.toLowerCase())
      );
    }

    if (filters.schoolArea) {
      filteredResult = filteredResult.filter((student) =>
        student.schoolArea.toLowerCase().includes(filters.schoolArea.toLowerCase())
      );
    }

    if (filters.schoolDistrict) {
      filteredResult = filteredResult.filter((student) =>
        student.schoolDistrict.toLowerCase().includes(filters.schoolDistrict.toLowerCase())
      );
    }

    setFilteredData(filteredResult);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const resetFilters = () => {
    setFilters({
      schoolName: '',
      age: '',
      caste: '',
      schoolArea: '',
      schoolDistrict: '',
    });
  };

  return (
    <div>
      <h2>Government Dashboard</h2>
      <div>
        <input
          type="text"
          placeholder="Filter by School Name"
          name="schoolName"
          value={filters.schoolName}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          placeholder="Filter by Age"
          name="age"
          value={filters.age}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          placeholder="Filter by Caste"
          name="caste"
          value={filters.caste}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          placeholder="Filter by School Area"
          name="schoolArea"
          value={filters.schoolArea}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          placeholder="Filter by School District"
          name="schoolDistrict"
          value={filters.schoolDistrict}
          onChange={handleFilterChange}
        />
        <button onClick={resetFilters}>Reset Filters</button>
      </div>
      <table>
        {/* Table headers */}
        <thead>
          <tr>
            <th>School Name</th>
            <th>Student Name</th>
            <th>Student Class</th>
            <th>Dropout Reason</th>
            <th>Age</th>
            <th>Caste</th>
            <th>School Area</th>
            <th>School District</th>
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {filteredData.map((student) => (
            <tr key={student._id}>
              <td>{student.schoolName}</td>
              <td>{student.studentName}</td>
              <td>{student.studentClass}</td>
              <td>{student.dropoutReason}</td>
              <td>{student.age}</td>
              <td>{student.caste}</td>
              <td>{student.schoolArea}</td>
              <td>{student.schoolDistrict}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default GovernmentDashboard;
