import React, { useEffect, useState } from "react";
import { Chart } from "react-google-charts";
import axios from "axios";

const options = {
  chart: {
    title: "School-wise Student Distribution",
    subtitle: "Number of Students in Each School",
  },
  hAxis: {
    title: "School Name",
  },
  vAxis: {
    title: "Number of Students",
  },
};

const VerticalBarGraph = () => {
  const [schoolData, setSchoolData] = useState([]);
  
  useEffect(() => {
    // Fetch school data from your backend API
    axios.get("http://localhost:3002/api/getSchoolData")
      .then((response) => {
        // Assuming your API response has an array of school objects
        setSchoolData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching school data:", error);
      });
  }, []);

  // Transform the fetched data into the format expected by the chart
  const chartData = [["School Name", "Number of Students"]];

  schoolData.forEach((school) => {
    chartData.push([school.schoolName, school.numberOfStudents]);
  });

  return (
    <Chart
      chartType="Bar"
      width="100%"
      height="400px"
      data={chartData}
      options={options}
    />
  );
};

export default SchoolBarGraph;
