import React, { useState } from 'react';
import axios from 'axios';
import { Container, Typography, TextField, Button, Box } from '@mui/material';
import "../SchoolForm.css";
function SchoolForm() {
  const [formData, setFormData] = useState({
    schoolName: '',
    studentName: '',
    studentClass: '',
    dropoutReason: '',
    age: '',
    caste: '',
    Religion:'',
    schoolArea: '',
    schoolDistrict: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if any field is empty before submission
    const isEmptyField = Object.values(formData).some((value) => value === '');

    if (isEmptyField) {
      alert('Please fill in all fields before submitting.');
      return;
    }

    try {
      await axios.post('http://localhost:3002/api/submitStudentData', formData);
      alert('Data submitted successfully.');
      // Clear all fields after successful submission
      setFormData({
        schoolName: '',
        studentName: '',
        studentClass: '',
        
        age: '',
        caste: '',
        Religion:'',
        schoolArea: '',
        schoolDistrict: '',
        dropoutReason: '',
      });
    } catch (error) {
      console.error(error);
      alert('Error submitting data.');
    }
  };

  return (
    <Container maxWidth="sm" className="school-container">
      <Box mt={4} p={4} boxShadow={2} borderRadius={4} className="whitebox">
        <Typography variant="h4" align="center" gutterBottom>
          School Portal
        </Typography>
        <form onSubmit={handleSubmit} className="school-form">
          <TextField
            fullWidth
            label="School Name"
            name="schoolName"
            value={formData.schoolName}
            onChange={handleChange}
            margin="normal"
            required // Mark the field as required
          />
          <TextField
            fullWidth
            label="Student Name"
            name="studentName"
            value={formData.studentName}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Student Class"
            name="studentClass"
            value={formData.studentClass}
            onChange={handleChange}
            margin="normal"
          />
          
          <TextField
            fullWidth
            label="Age"
            name="age"
            value={formData.age}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Caste"
            name="caste"
            value={formData.caste}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Religion"
            name="Religion"
            value={formData.caste}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="School Area"
            name="schoolArea"
            value={formData.schoolArea}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="School District"
            name="schoolDistrict"
            value={formData.schoolDistrict}
            onChange={handleChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Dropout Reason"
            name="dropoutReason"
            value={formData.dropoutReason}
            onChange={handleChange}
            margin="normal"
          />
         
          <Button type="submit" variant="contained" color="primary" fullWidth  className="school-submit-button">
            Submit
          </Button>
        </form>
      </Box>
    </Container>
  );
}

export default SchoolForm;
