import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';

function PieChart() {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    // Fetch data from API
    axios.get('http://localhost:3002/api/getStudentData')
      .then((response) => {
        const data = response.data;

        if (!data || data.length === 0) {
          // Handle the case where data is undefined or empty
          console.warn('No data available.');
          return;
        }

        // Calculate data for the pie chart
        const categories = {};
        data.forEach((student) => {
          const category = student.dropoutCategory || 'Unknown';
          categories[category] = (categories[category] || 0) + 1;
        });

        const chartData = {
          labels: Object.keys(categories),
          datasets: [
            {
              data: Object.values(categories),
              backgroundColor: [
                'rgba(255, 99, 132, 0.6)',
                'rgba(54, 162, 235, 0.6)',
                'rgba(255, 206, 86, 0.6)',
                'rgba(75, 192, 192, 0.6)',
              ],
            },
          ],
        };

        setChartData(chartData);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>Student Dropout Categories</h2>
      {chartData.labels && chartData.labels.length > 0 ? (
        <Pie data={chartData} />
      ) : (
        <p>No data available for the pie chart.</p>
      )}
    </div>
  );
}

export default PieChart;
