import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './LoginSignup.css'; // Import CSS file

function LoginSignup() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    userType: 'government', // Default user type
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });

  const handleToggle = () => {
    setIsLogin(!isLogin);
    clearFormFields(); // Clear form fields when toggling
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLogin) {
      try {
        const response = await axios.post('http://localhost:3001/api/login', {
          userType: formData.userType, // Include userType in login request
          email: formData.email,
          password: formData.password,
        });
        const { token } = response.data;
        // Handle successful login (e.g., store token in localStorage)
        console.log('Logged in with token:', token);
        window.alert('Login successful');
        clearFormFields(); // Clear form fields after login
      } catch (error) {
        console.error('Login error:', error.response.data.error);
        window.alert('Login failed');
      }
    } else {
      try {
        await axios.post('http://localhost:3001/api/signup', formData);
        // Handle successful signup (e.g., show success message)
        console.log('Signed up successfully');
        window.alert('Signup successful');
        clearFormFields(); // Clear form fields after signup
      } catch (error) {
        console.error('Signup error:', error.response.data.error);
        window.alert('Signup failed');
      }
    }
  };

  const clearFormFields = () => {
    setFormData({
      userType: 'government', // Reset user type to 'government'
      firstName: '',
      lastName: '',
      email: '',
      password: '',
    });
    const [userType, setUserType] = useState('');
  const history = useHistory();

  // Simulate a successful login/signup and set user type
  const handleLoginSignup = (type) => {
    setUserType(type);

    // Check if the userType is "school" and navigate to the school-form route
    if (type === 'school') {
      history.push('/school-form');
    } else if (type === 'government') {
      history.push('/government-dashboard'); // Redirect to the government-dashboard route
    }
  };

  return (
    <div className="login-signup-container">
      <div className="form-wrapper">
        <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
        <form className="form-container" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="userType">User Type:</label>
            <select
              id="userType"
              name="userType"
              value={formData.userType}
              onChange={handleChange}
              className="form-input"
            >
              <option value="government">Government</option>
              <option value="school">School</option>
            </select>
          </div>
          {!isLogin && (
            <>
              <div className="form-group">
                <label htmlFor="firstName">First Name:</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleChange}
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Last Name:</label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleChange}
                  className="form-input"
                />
              </div>
            </>
          )}
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-input"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-input"
            />
          </div>
          <button onClick={() => handleLoginSignup('school')}>Login/Signup as School</button>
      <button onClick={() => handleLoginSignup('government')}>Login/Signup as Government</button>
        </form>
        <p>
          {isLogin ? "Don't have an account? " : 'Already have an account? '}
          <span className="toggle" onClick={handleToggle}>
            {isLogin ? 'Sign Up' : 'Login'}
          </span>
        </p>
      </div>
    </div>
  );
}






  return (
    <div>
      {/* Your login/signup form */}
      <button onClick={() => handleLoginSignup('school')}>Login/Signup as School</button>
      <button onClick={() => handleLoginSignup('government')}>Login/Signup as Government</button>
    </div>
  );
}

export default LoginSignup;
