import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";

const SchoolBarChart = () => {
  const [chartData, setChartData] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Fetch student data from the API
    axios
      .get("http://localhost:3002/api/getStudentData")
      .then((response) => {
        const data = response.data;

        if (!data || data.length === 0) {
          console.warn("No data available.");
          setIsLoading(false);
          return;
        }

        // Calculate data for the vertical bar graph
        const schoolData = data.reduce((acc, student) => {
          const schoolName = student.schoolName || "Unknown";
          acc[schoolName] = (acc[schoolName] || 0) + 1;
          return acc;
        }, {});

        // Prepare data for the chart
        const chartData = {
          labels: Object.keys(schoolData),
          datasets: [
            {
              label: "Number of Students",
              data: Object.values(schoolData),
              backgroundColor: "rgba(75, 192, 192, 0.2)",
              borderColor: "rgba(75, 192, 192, 1)",
              borderWidth: 1,
            },
          ],
        };

        // Set new chart data
        setChartData(chartData);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setIsLoading(false);
      });
  }, []);

  const options = {
    scales: {
      x: {
        type: "category", // Use "category" scale for the x-axis
        title: {
          display: true,
          text: "School Names",
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Number of Students",
        },
      },
    },
  };

  // Function to destroy the previous chart when component unmounts
  useEffect(() => {
    return () => {
      if (chartData.datasets && chartData.datasets.length > 0) {
        // Destroy the chart to prevent canvas reuse
        chartData.datasets[0]._meta[0].data.forEach((dataPoint) => {
          dataPoint.hidden = true;
        });
      }
    };
  }, [chartData]);

  return (
    <div>
      <h2>School-wise Student Distribution (Vertical Bar Graph)</h2>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <Bar data={chartData} options={options} height={400} width={600} />
      )}
    </div>
  );
};

export default SchoolBarChart;
