import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";

const sch = () => {
  const [chartData, setChartData] = useState({});
  const [isLoading, setIsLoading] = useState(true);
  const [chartInstance, setChartInstance] = useState(null); // Store chart instance

  useEffect(() => {
    // Fetch student data from the API
    axios
      .get("http://localhost:3002/api/getStudentData")
      .then((response) => {
        const data = response.data;

        if (!data || data.length === 0) {
          console.warn("No data available.");
          setIsLoading(false);
          return;
        }

        // Calculate data for the vertical bar graph
        const schoolData = data.reduce((acc, student) => {
          const schoolName = student.schoolName || "Unknown";
          acc[schoolName] = (acc[schoolName] || 0) + 1;
          return acc;
        }, {});

        // Prepare data for the chart
        const chartData = {
          labels: Object.keys(schoolData),
          datasets: [
            {
              label: "Number of Students",
              data: Object.values(schoolData),
              backgroundColor: "rgba(75, 192, 192, 0.2)",
              borderColor: "rgba(75, 192, 192, 1)",
              borderWidth: 1,
            },
          ],
        };

        // Destroy existing chart if it exists
        if (chartInstance) {
          chartInstance.destroy();
        }

        // Set new chart data
        setChartData(chartData);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setIsLoading(false);
      });
  }, [chartInstance]); // Listen for changes in chartInstance

  const options = {
    scales: {
      x: {
        title: {
          display: true,
          text: "School Names",
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: "Number of Students",
        },
      },
    },
  };

  return (
    <div>
      <h2>School-wise Student Distribution (Vertical Bar Graph)</h2>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <Bar
          data={chartData}
          options={options}
          height={400}
          width={600}
          onElementsClick={(elems) => {
            // Handle chart element click events if needed
            console.log(elems);
          }}
          getChartInstance={(chart) => setChartInstance(chart)} // Store chart instance
        />
      )}
    </div>
  );
};

export default sch;
