import React, { useState } from 'react';
import axios from 'axios';

function SchoolPortal() {
  const [formData, setFormData] = useState({ studentName: '', studentClass: '', dropoutReason: '' });

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:3001/api/dropouts', formData); // Update the API endpoint URL
      // Clear the form after successful submission
      setFormData({ studentName: '', studentClass: '', dropoutReason: '' });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>School Portal</h2>
      <form>
        <div className="form-group">
          <label htmlFor="studentName">Student Name</label>
          <input
            type="text"
            className="form-control"
            id="studentName"
            placeholder="Enter student name"
            value={formData.studentName}
            onChange={(e) => setFormData({ ...formData, studentName: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="studentClass">Student Class</label>
          <input
            type="text"
            className="form-control"
            id="studentClass"
            placeholder="Enter student class"
            value={formData.studentClass}
            onChange={(e) => setFormData({ ...formData, studentClass: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="dropoutReason">Dropout Reason</label>
          <input
            type="text"
            className="form-control"
            id="dropoutReason"
            placeholder="Enter dropout reason"
            value={formData.dropoutReason}
            onChange={(e) => setFormData({ ...formData, dropoutReason: e.target.value })}
          />
        </div>
        <button type="button" className="btn btn-primary" onClick={handleSubmit}>
          Submit
        </button>
      </form>
    </div>
  );
}

export default SchoolPortal;
