import React, { useEffect, useState } from 'react';
import { Pie } from 'react-chartjs-2';
import axios from 'axios';

function PieChart() {
  const [data, setData] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:3002/api/getStudentData')
      .then((response) => {
        const students = response.data;
        const dropoutCategories = {};

        students.forEach((student) => {
          if (student.dropoutCategory) {
            if (dropoutCategories[student.dropoutCategory]) {
              dropoutCategories[student.dropoutCategory]++;
            } else {
              dropoutCategories[student.dropoutCategory] = 1;
            }
          }
        });

        const labels = Object.keys(dropoutCategories);
        const values = Object.values(dropoutCategories);

        const pieChartData = {
          labels,
          datasets: [
            {
              data: values,
              backgroundColor: [
                'rgba(255, 99, 132, 0.6)',
                'rgba(54, 162, 235, 0.6)',
                'rgba(255, 206, 86, 0.6)',
                'rgba(75, 192, 192, 0.6)',
                'rgba(153, 102, 255, 0.6)',
              ],
            },
          ],
        };

        setData(pieChartData);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>Student Dropout Categories</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <Pie data={data} />
      )}
    </div>
  );
}

export default PieChart;
