import React, { Component } from 'react';
import Chart from 'chart.js';

class PieChart extends Component {
  constructor(props) {
    super(props);
    this.data = [
      {
        title: 'Fruits',
        value: 50,
        color: '#ff0000',
      },
      {
        title: 'Vegetables',
        value: 30,
        color: '#00ff00',
      },
      {
        title: 'Meat',
        value: 20,
        color: '#0000ff',
      },
    ];
  }

  render() {
    const chartData = {
      labels: this.data.map(({ title }) => title),
      datasets: [
        {
          data: this.data.map(({ value }) => value),
          backgroundColor: this.data.map(({ color }) => color),
        },
      ],
    };

    return (
      <div>
        <Chart
          type="pie"
          data={chartData}
          options={{
            title: 'My Pie Chart',
          }}
        />
      </div>
    );
  }
}

export default PieChart;
