import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';

function PieChart() {
  const [loading, setLoading] = useState(true);
  const pieChartRef = useRef(null);

  useEffect(() => {
    // Fetch data from API
    axios.get('http://localhost:3002/api/getStudentData')
      .then((response) => {
        const data = response.data;

        // Calculate data for the pie chart
        const categories = {};
        data.forEach((student) => {
          const category = student.dropoutCategory || 'Unknown';
          categories[category] = (categories[category] || 0) + 1;
        });

        const chartData = {
          labels: Object.keys(categories),
          datasets: [
            {
              data: Object.values(categories),
              backgroundColor: [
                'rgba(255, 99, 132, 0.6)',
                'rgba(54, 162, 235, 0.6)',
                'rgba(255, 206, 86, 0.6)',
              ],
            },
          ],
        };

        // Destroy the previous chart if it exists
        if (pieChartRef.current) {
          pieChartRef.current.chartInstance.destroy();
        }

        // Render the new chart
        if (pieChartRef.current) {
          pieChartRef.current.chartInstance = new Chart(pieChartRef.current, {
            type: 'pie',
            data: chartData,
          });
        }

        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div>
      <h2>Student Dropout Categories</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <canvas ref={pieChartRef}></canvas>
      )}
    </div>
  );
}

export default PieChart;
