import React, { useState } from 'react';
import axios from 'axios';
import './LoginSignup.css'; // Import CSS file

function LoginSignup() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });
  const [alertMessage, setAlertMessage] = useState(null);

  const handleToggle = () => {
    setIsLogin(!isLogin);
    setAlertMessage(null); // Clear any existing alerts when toggling
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLogin) {
      try {
        const response = await axios.post('http://localhost:3001/api/login', formData);
        const { token } = response.data;
        // Handle successful login (e.g., store token in localStorage)
        console.log('Logged in with token:', token);
        setAlertMessage('Login successful'); // Set the success message
      } catch (error) {
        console.error('Login error:', error.response.data.error);
        setAlertMessage('Login failed'); // Set the error message
      }
    } else {
      try {
        await axios.post('http://localhost:3001/api/signup', formData);
        // Handle successful signup (e.g., show success message)
        console.log('Signed up successfully');
        setAlertMessage('Signup successful'); // Set the success message
      } catch (error) {
        console.error('Signup error:', error.response.data.error);
        setAlertMessage('Signup failed'); // Set the error message
      }
    }
  };

  return (
    <div className="login-signup-container">
      <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
      {alertMessage && <div className="alert">{alertMessage}</div>}
      <form className="form-container" onSubmit={handleSubmit}>
      <div className="form-group">
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="form-input"
          />
        </div>
        <button type="submit" className="form-button">
          {isLogin ? 'Login' : 'Sign Up'}
        </button>
      </form>
      <p>
      {isLogin ? "Don't have an account? " : 'Already have an account? '}
        <span className="toggle" onClick={handleToggle}>
          {isLogin ? 'Sign Up' : 'Login'}
        </span>
      </p>
    </div>
  );
}

export default LoginSignup;
