import React, { useState } from 'react';
import axios from 'axios';

function SchoolForm() {
  const [formData, setFormData] = useState({
    schoolName: '',
    studentName: '',
    studentClass: '',
    dropoutReason: '',
    age: '',
    caste: '',
    schoolArea: '',
    schoolDistrict: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3001/api/submitStudentData', formData); // Update the API endpoint URL
      alert('Data submitted successfully.'); // Display an alert on success
      setFormData({
        schoolName: '',
        studentName: '',
        studentClass: '',
        dropoutReason: '',
        age: '',
        caste: '',
        schoolArea: '',
        schoolDistrict: '',
      });
    } catch (error) {
      console.error(error);
      alert('Error submitting data.');
    }
  };

  return (
    <div>
      <h2>School Portal</h2>
      <form onSubmit={handleSubmit}>
        {/* ... (input fields) */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default SchoolForm;
